    private boolean validarData(String dataStr) {
    try {
        DateTimeFormatter formatter = DateTimeFormatter.ofPattern("dd/MM/yyyy");
        LocalDate.parse(dataStr, formatter);
        return true;
    } catch (DateTimeParseException e) {
        return false;
    }
}
