    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
 double diaria = 0;
    double vlrTotal = 0;
    double vlrDesconto = 0;
    String dataInicioStr = txtInicio.getText();
    String dataFimStr = txtFim.getText();

    // Validar se as datas estão no formato correto
    if (!validarData(dataInicioStr) || !validarData(dataFimStr)) {
        JOptionPane.showMessageDialog(this, "Por favor, insira as datas no formato dd/MM/aaaa.", "Erro", JOptionPane.ERROR_MESSAGE);
        return; // Impede o cálculo
    }

    // Obter a data atual
    LocalDate dataAtual = LocalDate.now();

    DateTimeFormatter formatter = DateTimeFormatter.ofPattern("dd/MM/yyyy");

    LocalDate dataInicio = LocalDate.parse(dataInicioStr, formatter);
    LocalDate dataFim = LocalDate.parse(dataFimStr, formatter);

    // Comparar a data de início com a data atual
    if (dataInicio.isBefore(dataAtual)) {
        JOptionPane.showMessageDialog(this, "A data de início não pode ser anterior à data atual.", "Erro", JOptionPane.ERROR_MESSAGE);
        return; // Impede o cálculo
    }

    long dias = ChronoUnit.DAYS.between(dataInicio, dataFim);
    int tipoBike = cbxBicicleta.getSelectedIndex();
    if (tipoBike == 0) {
        diaria = 25;       
    } else {
        diaria = 45;
    }

    int tipo = cbxTipo.getSelectedIndex();
    switch (tipo) {
        case 1:
            vlrTotal = diaria * dias;
            vlrDesconto = diaria * dias * 0.5;
            txtDias.setText(String.valueOf(dias));
            break;
        case 2:
            vlrTotal = diaria * dias;
            txtDias.setText(String.valueOf(dias));
            txtValor.setText("" + (vlrTotal - (diaria * dias * 0.1)));
            break;
        default:
            vlrTotal = diaria * dias;
            txtDias.setText(String.valueOf(dias));
            txtValor.setText("" + vlrTotal);
    }

    }//GEN-LAST:event_btnCalcularActionPerformed

